package com.example.exceptions;

class MyException extends Exception{
    private String info = null;

    public MyException(String message, double value) {
        super(message);
        info = "Funkcja próbuje ustawić wartość: "+value;
    }

    public String getInfo() {
        return info;
    }
}

public class App {
    double value;

    public static void main(String[] args) {
        App app = new App();

        try {
            app.setValue(-10);
        }catch(MyException e){
            System.out.println("Wyrzucono wyjątek: "+e.getMessage());
            System.out.println(e.getInfo());
            e.printStackTrace();

        }

        app.f1();

    }

    void f0() throws MyException{
        setValue(40);
    }

    void f1() {
        f0();
    }

    void setValue(double value) throws MyException{
        if(value<0)
            throw new MyException("Wartość nie może być ujemna", value);

        this.value = value;

        System.out.println("Ustawiam wartość: "+this.value);
    }
}
